mapper.select:
SELECT com03_day as com03Day, /** 일*/ com03_weekday as com03Weekday, /** 요일*/ com03_biz_yn as com03BizYn, /** 영업일여부*/ com03_note as com03Note /** 비고*/ FROM com03_date WHERE 1=1
ORDER BY com03_day
OFFSET #{offset}
LIMIT #{limit}

        SELECT MAX(com03_day)   as com03Day
          FROM (
		         SELECT com03_day
		           FROM com03_date
		          WHERE com03_biz_yn = 'true'
		            AND com03_day > #{stdDate}
		          ORDER BY com03_day
		          LIMIT #{days}
		       ) t

mapper.insert:
    INSERT INTO public.com03_date(
		com03_day, com03_weekday, com03_biz_yn, com03_note
	)
	VALUES (
		#{com03Day}, #{com03Weekday}, #{com03BizYn}, #{com03Note}
	)
