mapper.select:
SELECT A.jnl01_cont_id as jnl01ContId /** 체결ID */ , A.jnl01_seq as jnl01Seq /** 순번 */ , A.jnl01_dr_acnt_cd as jnl01DrAcntCd /** 차변계정코드 */ , A.jnl01_dr_amt as jnl01DrAmt /** 차변금액 */ , A.jnl01_cr_acnt_cd as jnl01CrAcnt_cd /** 대변계정코드 */ , A.jnl01_cr_amt as jnl01CrAmt /** 대변금액 */ /**차변 계정과목*/ , B.jnl10_acnt_cd as drJnl10AcntCd /**계정코드*/ , B.jnl10_acnt_nm as drJnl10AcntNm /**계정명*/ , B.jnl10_parent_cd as drJnl10ParentCd /**상위계정코드*/ , B.jnl10_acnt_attr_cd as drJnl10AcntAttrCd /**계정속성(acntattrcode)*/ , B.jnl10_drcr_type as drJnl10DrcrType /**차대구분(drcrtype)*/ , B.jnl10_slip_yn as drJnl10SlipYn /**전표생성여부*/ , B.jnl10_use_yn as drJnl10UseYn /**사용여부*/ /**대변 계정과목*/ , C.jnl10_acnt_cd as cRJnl10AcntCd /**계정코드*/ , C.jnl10_acnt_nm as cRJnl10AcntNm /**계정명*/ , C.jnl10_parent_cd as cRJnl10ParentCd /**상위계정코드*/ , C.jnl10_acnt_attr_cd as cRJnl10AcntAttrCd /**계정속성(acntattrcode)*/ , C.jnl10_drcr_type as cRJnl10DrcrType /**차대구분(drcrtype)*/ , C.jnl10_slip_yn as cRJnl10SlipYn /**전표생성여부*/ , C.jnl10_use_yn as cRJnl10UseYn /**사용여부*/ /** 운영지시*/ , D.opr01_cont_id as opr01ContId /** 체결ID */ , D.opr01_fund_cd as opr01FundCd /** 펀드코드 */ , D.opr01_item_cd as opr01ItemCd /** 종목코드 */ , D.opr01_cont_date as opr01ContDate /** 체결일자 */ , D.opr01_book_id as opr01BookId /**보유ID*/ , D.opr01_tr_cd as opr01TrCd /** 거래코드*/ , jnl12_tr_nm as opr01TrNm /**거래코드명*/
/** 펀드정보 */ , E.fnd01_fund_nm as fnd01FundNm /** 펀드명 */
/** 계정정보*/ , F.itm01_item_nm as itm01ItemNm /**종목명*/
FROM jnl01_journal A INNER JOIN opr01_cont D ON A.jnl01_cont_id = D.opr01_cont_id AND D.opr01_status_cd = '1' LEFT OUTER JOIN jnl10_acnt B ON A.jnl01_dr_acnt_cd = B.jnl10_acnt_cd LEFT OUTER JOIN jnl10_acnt C ON A.jnl01_cr_acnt_cd = C.jnl10_acnt_cd INNER JOIN fnd01_fund E ON D.opr01_fund_cd = E.fnd01_fund_cd INNER JOIN itm01_item F ON D.opr01_item_cd = F.itm01_item_cd INNER JOIN jnl12_tr ON (jnl12_tr_cd = opr01_tr_cd) WHERE 1=1
ORDER BY jnl01_cont_id, jnl01_seq
OFFSET #{offset}
LIMIT #{limit}

mapper.delete:
      DELETE FROM
      	jnl01_journal
      WHERE 1=1
	     AND jnl01_cont_id = #{jnl01ContId}
	     AND jnl01_seq     = #{jnl01Seq}
