mapper.update:
      UPDATE jnl02_journal_tmp 
         SET jnl02_cont_id      = #{jnl02ContId}
           , jnl02_seq          = #{jnl02Seq}
           , jnl02_drcr_type    = #{jnl02DrcrType}
           , jnl02_repr_acnt_cd = #{jnl02ReprAcntCd}
           , jnl02_amt          = #{jnl02Amt}
       WHERE jnl02_cont_id = #{jnl02ContId}
	     AND jnl02_seq     = #{jnl02Seq}

mapper.delete:
DELETE
  FROM jnl02_journal_tmp
 WHERE jnl02_cont_id = #{jnl02ContId}
   AND jnl02_seq     = #{jnl02Seq}
