mapper.select:
SELECT jnl10_acnt_cd as jnl10AcntCd, /**계정코드*/ jnl10_acnt_nm as jnl10AcntNm, /**계정명*/ jnl10_parent_cd as jnl10ParentCd, /**상위계정코드*/ jnl10_acnt_attr_cd as jnl10AcntAttrCd, /**계정속성(acntattrcode)*/ ( SELECT com02_code_nm /** 코드명*/ FROM com02_code WHERE 1=1 AND com02_com_cd = 'AcntAttrCode' AND com02_dtl_cd = A.jnl10_acnt_attr_cd AND COALESCE(com02_use_yn,'false') = 'true' ) as jnl10AcntAttrNm, jnl10_drcr_type as jnl10DrcrType, /**차대구분(drcrtype)*/ ( SELECT com02_code_nm /** 코드명*/ FROM com02_code WHERE 1=1 AND com02_com_cd = 'DrcrType' AND com02_dtl_cd = A.jnl10_drcr_type AND COALESCE(com02_use_yn,'false') = 'true' ) as jnl10DrcrTypeNm, jnl10_slip_yn as jnl10SlipYn, /**전표생성여부*/ jnl10_use_yn as jnl10UseYn /**사용여부*/ FROM jnl10_acnt A WHERE 1=1
ORDER BY jnl10_acnt_cd
OFFSET #{pageAttr.offset}
LIMIT #{pageAttr.limit}

mapper.insert:
INSERT INTO edu.jnl10_acnt(
	jnl10_acnt_cd, jnl10_acnt_nm, jnl10_parent_cd, jnl10_acnt_attr_cd, jnl10_drcr_type, jnl10_slip_yn, jnl10_use_yn
) VALUES( 
	#{jnl10AcntCd},#{jnl10AcntNm},#{jnl10ParentCd},#{jnl10AcntAttrCd},#{jnl10DrcrType},#{jnl10SlipYn},#{jnl10UseYn}
)
