mapper.select:
SELECT jnl12_tr_cd AS jnl12TrCd /* 거래코드 */ , jnl12_tr_nm AS jnl12TrNm /* 거래명 */ , jnl12_in_out_type AS jnl12InOutType /* 원장입출고구분(BookInOutType) */ , ( SELECT com02_code_nm /** 코드명*/ FROM com02_code WHERE 1=1 AND com02_com_cd = 'BookInOutType' AND com02_dtl_cd = A.jnl12_in_out_type AND COALESCE(com02_use_yn,'false') = 'true' ) as jnl12InOutTypeNm , jnl12_use_page_type AS jnl12UsePageType /* 사용화면구분 */ , jnl12_use_yn AS jnl12UseYn /* 사용여부 */ FROM jnl12_tr A WHERE 1=1
ORDER BY jnl12_tr_cd
OFFSET #{pageAttr.offset}
LIMIT #{pageAttr.limit}
