mapper.select:
SELECT A.jnl13_tr_cd AS jnl13TrCd /* 거래코드 */ , A.jnl13_seq AS jnl13Seq /* 순번 */ , A.jnl13_repr_acnt_cd AS jnl13ReprAcntCd /* 대표계정코드 */ , A.jnl13_drcr_type AS jnl13DrcrType /* 차대구분(DrcrType) */ , ( SELECT com02_code_nm /** 코드명*/ FROM com02_code WHERE 1=1 AND com02_com_cd = 'DrcrType' AND com02_dtl_cd = A.jnl13_drcr_type AND COALESCE(com02_use_yn,'false') = 'true' ) as jnl13DrcrTypeNm , A.jnl13_formula AS jnl13Formula /* 계산식 */ , C.jnl11_repr_acnt_nm as jnl11ReprAcntNm /* 대표계정명 */ FROM jnl13_tr_map A inner join edu.jnl12_tr B on A.jnl13_tr_cd = B.jnl12_tr_cd left outer join edu.jnl11_repr_acnt C on A.jnl13_repr_acnt_cd = C.jnl11_repr_acnt_cd WHERE 1=1
ORDER BY A.jnl13_tr_cd, A.jnl13_seq
OFFSET #{pageAttr.offset}
LIMIT #{pageAttr.limit}

SELECT jnl13_tr_cd AS jnl13TrCd /* 거래코드 */ , (SELECT jnl12_tr_nm FROM edu.jnl12_tr WHERE jnl12_tr_cd = A.jnl13_tr_cd) AS jnl13TrNm , jnl13_seq AS jnl13Seq /* 순번 */ , jnl13_repr_acnt_cd AS jnl13ReprAcntCd /* 대표계정코드 */ , jnl13_drcr_type AS jnl13DrcrType /* 차대구분(DrcrType) */ ,( SELECT com02_code_nm /** 코드명*/ FROM com02_code WHERE 1=1 AND com02_com_cd = 'DrcrType' AND com02_dtl_cd = A.jnl13_drcr_type AND COALESCE(com02_use_yn,'false') = 'true' ) as jnl13DrcrTypeNm , jnl13_formula AS jnl13Formula /* 계산식 */ , C.jnl11_repr_acnt_nm as jnl11ReprAcntNm /* 대표계정명 */ FROM jnl13_tr_map A left outer join edu.jnl11_repr_acnt C on A.jnl13_repr_acnt_cd = C.jnl11_repr_acnt_cd WHERE 1=1
AND jnl13_tr_cd = #{jnl13TrCd}
AND jnl13_seq = #{jnl13Seq}

mapper.update:
      UPDATE  
      	jnl13_tr_map 
      SET
			  jnl13_tr_cd        = #{jnl13TrCd}       
			, jnl13_seq          = #{jnl13Seq}        
			, jnl13_repr_acnt_cd = #{jnl13ReprAcntCd} 
			, jnl13_drcr_type    = #{jnl13DrcrType}   
			, jnl13_formula      = #{jnl13Formula}
	   WHERE 1=1
	       	AND jnl13_tr_cd = #{jnl13TrCd}
	       	AND jnl13_seq = #{jnl13Seq}

mapper.delete:
DELETE FROM
	jnl13_tr_map
WHERE 1=1
	 AND jnl13_tr_cd = #{jnl13TrCd}
	 AND jnl13_seq = #{jnl13Seq}
