mapper.select:
SELECT jnl14_repr_acnt_cd AS jnl14ReprAcntCd , jnl11_repr_acnt_nm AS jnl14ReprAcntNm , jnl14_list_type AS jnl14ListType , jnl14_list_type_nm AS jnl14ListTypeNm , jnl14_market_type AS jnl14MarketType , jnl14_market_type_nm AS jnl14MarketTypeNm , jnl14_real_acnt_cd AS jnl14RealAcntCd , jnl10_acnt_nm AS jnl14RealAcntNm FROM ( SELECT A.jnl14_repr_acnt_cd , ( SELECT jnl11_repr_acnt_nm FROM edu.jnl11_repr_acnt WHERE jnl11_repr_acnt_cd = A.jnl14_repr_acnt_cd ) as jnl11_repr_acnt_nm , A.jnl14_list_type , ( SELECT com02_code_nm /** 코드명*/ FROM com02_code WHERE 1=1 AND com02_com_cd = 'ListType' AND com02_dtl_cd = A.jnl14_list_type AND COALESCE(com02_use_yn,'false') = 'true' ) AS jnl14_list_type_nm , A.jnl14_market_type , ( SELECT com02_code_nm /** 코드명*/ FROM com02_code WHERE 1=1 AND com02_com_cd = 'MarketType' AND com02_dtl_cd = A.jnl14_market_type AND COALESCE(com02_use_yn,'false') = 'true' ) AS jnl14_market_type_nm , A.jnl14_real_acnt_cd ,( SELECT jnl10_acnt_nm FROM edu.jnl10_acnt WHERE jnl10_acnt_cd = A.jnl14_real_acnt_cd ) AS jnl10_acnt_nm FROM jnl14_real_acnt_map A ) t WHERE 1=1
ORDER BY jnl14_repr_acnt_cd, jnl14_list_type, jnl14_market_type
OFFSET #{pageAttr.offset}
LIMIT #{pageAttr.limit}

SELECT jnl14_real_acnt_cd
  FROM jnl14_real_acnt_map
 WHERE jnl14_repr_acnt_cd   = #{reprAcntCode}
   AND jnl14_list_type      = #{listType}
   AND jnl14_market_type    = #{marketType}

mapper.update:
      UPDATE  
      	jnl14_real_acnt_map 
      SET
		  jnl14_repr_acnt_cd = #{jnl14ReprAcntCd} 
		, jnl14_list_type    = #{jnl14ListType}   
		, jnl14_market_type  = #{jnl14MarketType} 
		, jnl14_real_acnt_cd = #{jnl14RealAcntCd}
	   WHERE 1=1
	       	AND jnl14_repr_acnt_cd = #{jnl14ReprAcntCdOld}
	       	AND jnl14_list_type = #{jnl14ListTypeOld}
	       	AND jnl14_market_type = #{jnl14MarketTypeOld}

mapper.delete:
      DELETE FROM
      	jnl14_real_acnt_map
      WHERE 1=1
	       	AND jnl14_repr_acnt_cd = #{jnl14ReprAcntCd}
	       	AND jnl14_list_type = #{jnl14ListType}
	       	AND jnl14_market_type = #{jnl14MarketType}
